(290, 'fs_active_plugins', 'O:8:\"stdClass\":3:{s:7:\"plugins\";a:1:{s:30:\"olivewp-companion/inc/freemius\";O:8:\"stdClass\":4:{s:7:\"version\";s:5:\"2.4.3\";s:4:\"type\";s:6:\"plugin\";s:9:\"timestamp\";i:1664458194;s:11:\"plugin_path\";s:39:\"olivewp-companion/olivewp-companion.php\";}}s:7:\"abspath\";s:25:\"C:\\laragon\\www\\CTesp-DWM/\";s:6:\"newest\";O:8:\"stdClass\":5:{s:11:\"plugin_path\";s:39:\"olivewp-companion/olivewp-companion.php\";s:8:\"sdk_path\";s:30:\"olivewp-companion/inc/freemius\";s:7:\"version\";s:5:\"2.4.3\";s:13:\"in_activation\";b:0;s:9:\"timestamp\";i:1664458194;}}', 'yes'),
